import React from 'react'
import styled from 'styled-components';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import Nav from '../Components/Nav/Nav';


const SignInCard = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    background: white;
    padding: 50px;
    box-shadow: 5px 5px 15px -3px rgba(0,0,0,0.64);
`;

const FormLabel = styled.label`
    margin-top: 15px;
    border: 1px solid black;
    border-radius: 20px;
    padding: 5px 0;
`;

const FormInput = styled.input`
    padding: 3px 8px;
    border: none;
    border-top-right-radius: 18px;
    border-bottom-right-radius: 18px;

    &: focus {
        outline: none;
    }
`;

const SignInBtn = styled.button`
    border-style: none;
    width: 120px;
    height: 45px;
    background: linear-gradient(132deg, rgba(13,13,13,1) 0%, rgba(33,33,33,1) 100%);
    color: white;
    transition: all .2s ease;
    border-radius: 15px;
    margin: 20px;

    &:hover {
        opacity: 0.8;
    }

    &:hover .arrow-right-sign-in {
        transform: translate(10px, 0px);
    }
`;

function SignIn() {
    return (
        <>
        <Nav/>
        <div style={{height: 'calc(100vh - 100px)', display: 'flex', justifyContent: 'center', alignItems: 'center'}}>
            <form  >
                <SignInCard>
                    <p style={{fontSize: '28px', marginBottom: '15px', padding: '30px'}}>Sign In</p>
                    <FormLabel>
                        <FontAwesomeIcon icon='user' />
                        <FormInput type='email' placeholder='Email'/>
                    </FormLabel>
                    <FormLabel>
                        <FontAwesomeIcon icon='lock' />
                        <FormInput type='password' placeholder='Password' />
                    </FormLabel>

                    <SignInBtn>Sign In <FontAwesomeIcon icon='arrow-right' className='arrow-right-sign-in' /></SignInBtn>

                </SignInCard>
            </form>
        </div>

        </>
    )
}

export default SignIn
