import React, { Component } from 'react';
import Hero from '../../Components/Hero';
import Nav from '../../Components/Nav/Nav';
import styled from 'styled-components';

import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

import airJordan from '../../Resources/air-jordan.png';
import airMax from '../../Resources/air-max.png';
import airForce from '../../Resources/air-force.png';

import airJordanHover from '../../Resources/air-jordan-hover.png';
import airMaxHover from '../../Resources/air-max-hover.png';
import airForceHover from '../../Resources/air-force-hover.jpg';

const DeckContainer = styled.div`
    height: 55vh;
    display: flex;
    flex-direction: column;
    flex-wrap: wrap;
    justify-content: center;
    align-items: center;
    background-color: black;
`;

const BestSellerTitle = styled.p`
    font-family: "Lucida Console", Monaco, monospace;
    font-size: 1.4rem;
    color: white;
`;

const CardDeck = styled.div`
    display: flex;
    justify-content: center;
    align-items: center;
    flex-wrap: wrap;
    background-color: black;
`;

const Card = styled.div`
    height: 270px;
    width: 400px;
    display: grid;
    grid-template-columns: 1.15fr 1fr;
    margin: 20px;
    background-image: url(${props => props.bg});
    background-repeat: no-repeat;
    background-position: center;
    background-size: 400px 300px;
    object-fit: cover;
    border-radius: 5px;
    cursor: pointer;
    &:hover {
        background-image: url(${props => props.bgHover});
    };
    &:hover .featured-collection-title {
        color: #7f1e1d;
        transform: translate(3px);
    };
    &:hover .see-collection {
        color: #7f1e1d;
        transform: translate(10px);
    };
`;

const CardTextContainer = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    text-align: center;
`;

const SeeCollection = styled.p`
    color: ${props => props.color || 'rgb(211, 211, 211)'};
    margin: 20px;
    cursor: pointer;
    transition: all 0.3s ease-in-out;
    // &:hover {
    //     transform: translate(10px);
    //     color: black;
    // }
`;

class Home extends Component {
    // componentDidMount = () => {
    // }

    render() {
        return (
            <>
                <Nav />
                <Hero />
                <div style={{height: '100vh', backgroundColor: 'black'}}></div>
            </>
        )
    }
}

export default Home;
