import React from 'react';
import { useState, useEffect } from 'react';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import styled from 'styled-components';



const FilterButton = styled.p`
    color: ${props => props.color};
    background-color: ${props => props.bg};
    font-size: 14px;
    padding: 5px 23px;
    border: 2px solid black;
    border-radius: 15px;
    font-weight: 550;
    width: fit-content;
    cursor: pointer;
    transition: all 0.3s ease;
    &:hover {
        color: white;
        background-color: black;
    }
`;

const HR  = styled.p`
    margin-top: 15px;
    border-top: solid black 2px;
    width: 300px;
`;

const CheckboxOption = styled.input`
    height: 18px;
    width: 18px;
    color: black;
`;

function Filters() {
    const [clicked, handleClick] = useState(false);

    return (
        <div style={{display: 'flex', flexDirection: 'column', width: '100%', justifyContent: 'center', alignItems: 'center'}}>

            <div>
                <div>
                    <label>Sizing</label>
                    <div>
                        <CheckboxOption type='checkbox'></CheckboxOption>
                        <label>Mens</label>
                    </div>
                    <div>
                        <input type='checkbox'></input>
                        <label>Womens</label>
                    </div>
                    <div>
                        <input type='checkbox'></input>
                        <label>Kids</label>
                    </div>
                </div>
            </div>

            <HR></HR>
            <FilterButton color={clicked ? 'white' : 'black'} bg={clicked ? 'black' : 'white'} onClick={() => handleClick((!clicked))}>Filters   <FontAwesomeIcon icon={clicked ? 'caret-up' : 'caret-down'} /></FilterButton>
        </div>
    )
}

export default Filters
