import React, { useState, useEffect } from 'react';
import  { Swiper , SwiperSlide}  from 'swiper/react';
import SwiperCore, { Navigation, Mousewheel } from 'swiper';
import 'swiper/swiper-bundle.css';
import styled from 'styled-components';

const Wrapper = styled.div`
    width: 100%;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    background-color: black;
`;

const CategorySectionTitle = styled.p`
    width: 70%;
    font-size: 1.4rem;
    color: white;
    margin-bottom: 0;
    background-color: black;
    text-align: center;
    padding: 25px;
    padding-top: 50px;
    border-top: 1px solid white;
`;

const Category = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    text-align: center;
    cursor: pointer;
    &:hover .category-img {
        transform: scale(1.05);
    }
`;

const Image = styled.img`
    object-fit: cover;
    height: 166px;
    width: 166px;
    transition: all .2s ease;
    // @media screen and (max-width: 1400px) {
    //     height: 150px;
    //     width: 150px;
    // }

    @media screen and (max-width: 1232px) {
        height: 130px;
        width: 130px;
    }

    // @media screen and (max-width: 1100px) {
    //     height: 120px;
    //     width: 120px;
    // }

    // @media screen and (max-width: 630px) {
    //     height: 80px;
    //     width: 80px;
    // }

    // @media screen and (max-width: 500px) {
    //     height: 60px;
    //     width: 60px;
    // }
`;

const ImageWrapper = styled.div`
    display: inline-block;
    overflow: hidden;  
    border-radius: 50%;
    margin-bottom: 10px
`;

const categories = [
    {
        category: 'Men',
        img: 'https://i8.amplience.net/i/jpl/jd_368699_a?qlt=92&w=950&h=1212&v=1&fmt=webp'
    },
    {
        category: 'Women',
        img: 'https://static.nike.com/a/images/c_limit,w_592,f_auto/t_product_v1/241c485a-27a6-46cb-b951-2acc64563205/sportswear-tech-fleece-windrunner-hoodie-3jcHXn.png'
    },
    {
        category: 'Kids',
        img: 'https://static.nike.com/a/images/t_PDP_1280_v1/f_auto,q_auto:eco/5b9b0acb-e5a1-4bb2-a9fb-7fb395a4460e/sportswear-tech-fleece-hoodie-and-trousers-set-0X3zlT.png'
    },
    {
        category: 'Lifestyle',
        img: 'https://static.nike.com/a/images/c_limit,w_592,f_auto/t_product_v1/a31b545c-5007-417a-8b15-a722ae69d76c/custom-nike-blazer-mid-77-vintage-by-you.png'
    },
    {
        category: 'Jordan',
        img: 'https://static.nike.com/a/images/c_limit,w_592,f_auto/t_product_v1/f869a753-bef6-4164-b2a0-470274914a29/jordan-max-aura-2-shoe-df4XZl.png'
    },
    {
        category: 'Running',
        img: 'https://hips.hearstapps.com/vader-prod.s3.amazonaws.com/1589987097-zoomx-vaporfly-next-running-shoe-Vt6Lh9.jpg'
    },
    {
        category: 'Football',
        img: 'https://static.nike.com/a/images/c_limit,w_592,f_auto/t_product_v1/4461a557-265f-4e3a-9b43-b54a1c90054c/mercurial-superfly-8-elite-fg-football-boots-s56jRp.png'
    },
    {
        category: 'Accessories',
        img: 'https://www.yoox.com/images/items/46/46740962CG_14_f.jpg?impolicy=crop&width=387&height=490'
    }
];

SwiperCore.use([Navigation]);

function SwiperCategories() {

    const [widthSize, setWidthSize] = useState(window.innerWidth <= 650 ? 'S' : window.innerWidth <= 1232 ? 'M' : 'L');

    const handleResize = () => {
        if (window.innerWidth <= 650) setWidthSize('S');
        else if (window.innerWidth <= 1232) setWidthSize('M');
        else setWidthSize('L')
    }

    useEffect(() => {
        window.addEventListener("resize", handleResize, false);
    }, []);

    return (
        <Wrapper>
            <CategorySectionTitle >Shop By Category</CategorySectionTitle>
            <Swiper
            speed={500}
            slidesPerView={widthSize === 'S' ? 2 : widthSize === 'M' ? 3 : 5}
            spaceBetween={10}
            navigation
            mousewheel
            autoplay
            style={{backgroundColor: 'black'}}
            >
                {
                    categories.map((category, i) => {
                        return (
                            <SwiperSlide>
                                <Category>
                                    <ImageWrapper>
                                        <Image className='category-img' src={category.img} />
                                    </ImageWrapper>
                                    <h5 style={{color: 'white', margin: '10px'}}>{category.category}</h5>
                                </Category>
                            </SwiperSlide>
                        );
                    })
                }
            </Swiper>
        </Wrapper>
    )
}

export default SwiperCategories
