import React, { useState, useEffect } from 'react'
import  { Swiper , SwiperSlide}  from 'swiper/react';
import SwiperCore, { Scrollbar } from 'swiper';
import 'swiper/swiper-bundle.css';
import styled from 'styled-components';


const Wrapper = styled.div`
    width: 100%;
    background-color: white;
    padding-bottom: 70px;
`;

const CategorySectionTitle = styled.p`
    font-size: 1.4rem;
    color: black;
    margin-bottom: 0;
    background-color: white;
    padding: 40px;
    padding-left: 80px;
    margin-top: 50px;
    font-weight: 600;
    @media (max-width: 935px) {
        text-align: center;
        padding-left: 0;
    }
`;

const ItemCard = styled.div`
    height: 380px;
    width: 270px;
    cursor: pointer;

    &:hover .featured-item-img {
        transform: scale(1.03);
    }
    &:hover .featured-item-detail {
        color: black;
    }
`;

SwiperCore.use([Scrollbar]);

const ImageWrapper = styled.div`
    display: inline-block;
    overflow: hidden;  
`;

const ItemImage = styled.img`
    height: 340px;
    width: 270px;
    object-fit: cover;
    transition: all .4s ease;
`;

const ItemDetail = styled.p`
    font-size: 1.1rem;
    margin: 0;
    color: rgb(75, 74, 74);
    transition: all .3s ease;
`;

const itemCardsData = [
    {
        name: `Air Force 1 Mid '07`,
        img: 'https://static.nike.com/a/images/t_PDP_864_v1/f_auto,b_rgb:f5f5f5/04421d64-7214-4718-ba73-93e6d1447bac/air-force-1-mid-07-shoe-Nz7W5f.png',
        price: 109.95
    },
    {
        name: 'Joggers',
        img: 'https://static.nike.com/a/images/q_auto:eco/t_product_v1/f_auto/dpr_2.0/w_317,c_limit/uine1pshjr41hoyh1qvj/sportswear-club-fleece-joggers-6wr1Jb.png',
        price: 30
    },
    {
        name: 'Shorts',
        img: '',
        price: 30
    },
    {
        name: 'Jacket',
        img: '',
        price: 85
    },
    {
        name: 'Cap',
        img: '',
        price: 12
    },
    {
        name: 'Sliders',
        img: '',
        price: 25
    },
    {
        name: '',
        img: '',
        price: 50
    },
    {
        name: 'Tee Shirt',
        img: '',
        price: 50
    }
]; 

function FeaturedProducts() {

    const [widthSize, setWidthSize] = useState(window.innerWidth <= 650 ? 'S' : window.innerWidth <= 1232 ? 'M' : 'L');

    const handleResize = () => {
        if (window.innerWidth <= 650) setWidthSize('S');
        else if (window.innerWidth <= 1232) setWidthSize('M');
        else setWidthSize('L')
    }

    useEffect(() => {
        window.addEventListener("resize", handleResize, false);
    }, []);

    return (
        <Wrapper>
            <CategorySectionTitle>Gear Up</CategorySectionTitle>
            <Swiper
                scrollbar={{ draggable: true }}
                speed={500}
                slidesPerView={widthSize === 'L' ? 4 : widthSize === 'M' ? 2 : 1}
                spaceBetween={20}
                style={{backgroundColor: 'white', paddingBottom: '80px'}}
            >
                {
                    itemCardsData.map((item, i) => {
                        return (
                            <SwiperSlide>
                                <ItemCard>
                                    <ImageWrapper>
                                        <ItemImage src={item.img} className='featured-item-img'/>
                                    </ImageWrapper>
                                    <ItemDetail className='featured-item-detail'>{item.name}</ItemDetail>
                                    <ItemDetail className='featured-item-detail'>Â£{item.price}</ItemDetail>
                                </ItemCard>
                            </SwiperSlide>
                        )
                    })
                }
            </Swiper>

        </Wrapper>
    )
}

export default FeaturedProducts
