import React, { useState, useEffect } from 'react'
import  { Swiper , SwiperSlide}  from 'swiper/react';
import SwiperCore, { Navigation, Mousewheel, Autoplay } from 'swiper';
import 'swiper/swiper-bundle.css';
import styled from 'styled-components';

SwiperCore.use([Navigation, Mousewheel, Autoplay]);

const Wrapper = styled.div`
    width: 100%;
    // text-align: center;
    background-color: white;
    padding-bottom: 70px;
`;

const CategorySectionTitle = styled.p`
    display: inline-block;
    width: 70%;
    font-size: 1.4rem;
    color: black;
    margin-bottom: 0;
    background-color: white;
    padding: 40px;
    margin-top: 50px;
    // border-top: 1px solid black;
    font-weight: 600;
`;

const ItemCard = styled.div`
    height: 340px;
    width: 270px;
    background-color: grey;
`;

function FeaturedProducts() {

    const [widthSize, setWidthSize] = useState(window.innerWidth <= 650 ? 'S' : window.innerWidth <= 1232 ? 'M' : 'L');

    const handleResize = () => {
        if (window.innerWidth <= 650) setWidthSize('S');
        else if (window.innerWidth <= 1232) setWidthSize('M');
        else setWidthSize('L')
    }

    useEffect(() => {
        window.addEventListener("resize", handleResize, false);
    }, []);

    return (
        <Wrapper>
            <CategorySectionTitle>Gear Up</CategorySectionTitle>
            <Swiper
                scrollbar={{ draggable: true }}
                mousewheel
                speed={500}
                autoplay
                slidesPerView={widthSize === 'L' ? 4 : widthSize === 'M' ? 2 : 1}
                spaceBetween={20}
                style={{backgroundColor: 'white', paddingBottom: '50px'}}
            >
                <SwiperSlide>
                    <ItemCard>

                    </ItemCard>
                </SwiperSlide>
                <SwiperSlide>
                    <ItemCard>

                    </ItemCard>
                </SwiperSlide>
                <SwiperSlide>
                    <ItemCard>

                    </ItemCard>
                </SwiperSlide>
                <SwiperSlide>
                    <ItemCard>

                    </ItemCard>
                </SwiperSlide>
            </Swiper>

        </Wrapper>
    )
}

export default FeaturedProducts
