import { useState } from 'react';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';
import Home from './Pages/Home';
import Shop from './Pages/Shop';
import ItemPage from './Pages/ItemPage';
import SignIn from './Pages/SignIn';
import SignUp from './Pages/SignUp';
import ShoppingBag from './Pages/ShoppingBag';
import Nav from './Components/Nav/Nav';
import './Components/FontawesomeIcons';
import './App.css';

const addItemToBag = item => {
  updateBagItems([...bagItems, item])
}


function App() {

  const [bagItems, updateBagItems] = useState([]);


  return (
    <Router>
      <Nav bagItems={bagItems}/>
      <Switch>
        <Route path='/' component={Home} exact/>
        <Route path='/shop' component={Shop} exact/>
        <Route path='/shop/:id' render={props => <ItemPage addItemToBag={addItemToBag} {...props} />} />
        <Route path='/signin' component={SignIn} exact/>
        <Route path='/register' component={SignUp} exact/>
        <Route path='/bag' render={props => <ShoppingBag bagItems={bagItems} {...props} />} exact/>
      </Switch>
    </Router>
  );
}

export default App;
