        //     }
        // })
        // .then(resp => resp.json())
        // .then(data => {
        //     console.log(data);
        // })
        // .catch(err => {
        //     console.error(err);
        // });    
    }

    render() {
        return (
            <>
                <Nav />
                <Hero /> 
                <CardDeck>
                    <Card>
                        <CardImg src={i1} />
                        <h4>Air Force</h4>
                        <div>
                            <CardButton>View All</CardButton>
                        </div>
                    </Card>
                    <Card>
                        <CardImg src={heroBg2}/>
                        <h3>Air Max</h3>
                    </Card>
                    <Card>
                        <CardImg src={heroBg3}/>
                        <h3>Air Force</h3>
                    </Card>
                </CardDeck>


            </>
        )
    }
}

export default Home;
