import React, {useEffect, useState} from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import './Nav.css';

const AccountNav = styled.div`
	min-height: 35px;
	background-color: #dedede;
	display: flex;
	justify-content: flex-end;
	align-items: center;
`;

const JoinUs = styled.p`
	margin: 0;
	margin-right: 10px;
	font-size: 0.8rem;
	height: 100%;
	&:hover {
		color: #595959;
	}
`;

const SignIn = styled.p`
	margin: 0px 20px 0px 10px;
	font-size: 0.8rem;
	height: 100%;
	&:hover {
		color: #595959;
	}
`;


function Nav() {

	const [minimized, setMinimize] = useState(window.innerWidth <= 960);
	const [clicked, setClick] = useState(false);

	const handleResize = () => {
		if (window.innerWidth > 960) {
			setMinimize(false);
			setClick(false);
		}
		else {
			setMinimize(true);
		}
	}

	useEffect(() => {
		window.addEventListener("resize", handleResize, false);
	}, []);

    return (
		<>	
			<div className='nav-wrapper'>
				<div className={clicked ? 'active' : !clicked && minimized ? 'idle' : 'main'}>
					<Link style={{marginTop: '10px', textDecoration: 'none', fontSize: '22px', color: 'black', cursor: 'pointer', marginLeft: '20px', fontWeight: '500', display: 'inline-block'}}>Sneaker Galore</Link>
					<FontAwesomeIcon icon={clicked ? 'times' : 'bars'} onClick={() => setClick(!clicked)} className={minimized ? 'bars' : 'bars-main'}/>


					<ul className={clicked ? 'active-nav' : !clicked && minimized ? 'idle-nav' : 'main-nav'}>
						<li className={clicked ? 'active-link' : 'main-link'}>Home</li>
						<li className={clicked ? 'active-link' : 'main-link'}>Collections</li>
						<li className={clicked ? 'active-link' : 'main-link'}>Shop</li>
					</ul>

					<ul className={clicked ? 'active-nav' : !clicked && minimized ? 'idle-nav' : 'main-nav'}>
						<li className={clicked ? 'active-link' : 'main-link'}><input type='text' /></li>
						<li className={clicked ? 'active-link' : 'main-link'}>
							<FontAwesomeIcon icon='shopping-bag' className={clicked ? 'active-nav-icon' : 'nav-icon'} id='nav-shopping-bag'/>
							<FontAwesomeIcon icon='heart' className='nav-icon'/>
						</li>
					</ul>


				</div>

				<AccountNav>
					<Link
						style={{textDecoration: 'none', color: 'black', height: '100%'}}
					><JoinUs>Join Us</JoinUs></Link>
					|
					<Link
						style={{textDecoration: 'none', color: 'black'}}
					><SignIn>Sign In</SignIn></Link>
				</AccountNav>
			</div>
		</>
    );
}

export default Nav;
