import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import React, { useState} from 'react'
import { Link } from 'react-router-dom';
import styled from 'styled-components';

const ItemGrid = styled.div`
    display: grid;
    grid-template-columns: 500px 100px 100px 50px;
    grid-gap: 10px;
    margin-bottom: 20px;
    align-items: center;
`;

const QtySelector = styled.select`
    color: #404040;
    width: 60px;
    height: 32px;
    font-size: 18px;
    text-align: center;
    // border: 1px solid grey;
    border: none;
    background-color: #f6f6f6;
    border-radius: 6px;
    cursor: pointer;

    &:hover {
        // border: 1px solid black;
    }


    &:focus {
        outline: none;
    }
`;

const DisplayedItemPrice = styled.p`
    color: #404040;
    width: fit-content;
    height: 32px;
    line-height: 32px;
    padding: 0px 9px;
    font-size: 18px;
    text-align: center;
    // border: 1px solid grey;
    border: none;
    background-color: #f6f6f6;
    border-radius: 6px;
    cursor: pointer;
    margin-bottom: 0;

    &:hover {
        // border: 1px solid black;
    }


    &:focus {
        outline: none;
    }
`;

const CheckoutWrapper = styled.div`
    width: 400px;
    margin: 5%;
    display: flex;
    flex-direction: column;
    align-items: center;
    border: 1px solid black;
`;

const CheckoutRow = styled.div`
    display: flex;
    justify-content: space-between;
    width: 80%;
    margin: 15px;
`;

function ShoppingBag(props) {

    const { bagItems, changeQty, deleteItem } = props;
    const [shipping, changeShipping] = useState('standard');

    let itemsTotal = 0;

    let total = itemsTotal + shipping;
    return (
        <div style={{minHeight: 'calc(100vh - 120px)', display: 'flex', justifyContent: 'center', alignItems: 'start', flexWrap: 'wrap'}}>
            <div style={{margin: '5%'}}>
                <h1 style={{marginBottom: '30px'}}>Shopping Bag</h1>
                <div style={{marginTop: '30px', borderTop: 'solid rgb(224, 224, 224) 4px', paddingTop: '20px'}}>
                    {
                        bagItems.length < 1 ?
                        <p>Your shopping bag is empty</p>
                        :
                        <ItemGrid>
                            <strong><p>PRODUCT DETAILS</p></strong>
                            <strong><p>QTY</p></strong>
                            <strong><p>TOTAL</p></strong>
                        </ItemGrid>
                    }
                    {
                        bagItems.map((item, i) => {
                            itemsTotal += (item.quantity * item.price)
                            return(
                                <ItemGrid>
                                    <div style={{display: 'flex'}}>
                                        <img src={item.img[0]} style={{height: '130px', width: '130px', objectFit: 'contain'}} alt='shoe'/>
                                        <div style={{display: 'flex', flexDirection: 'column', justifyContent: 'space-between', marginLeft: '10px'}}>
                                            <div style={{display: 'flex', flexDirection: 'column', fontSize: '16px'}}>
                                                <strong><p style={{fontSize: '18px'}}>{item.name}</p></strong>
                                                <p style={{marginBottom: '5px'}}>Size: {item.size}</p>
                                                <p style={{marginBottom: '5px'}}>£{item.price}</p>
                                            </div>
                                            {/* <a  onClick={() => deleteItem(item.id, i)} style={{fontSize: '15px', color: 'black', borderBottom: '1px solid black', width: 'fit-content', cursor: 'pointer'}}>Delete</a> */}
                                        </div>
                                    </div>
                                    <div>
                                        <QtySelector onChange={e => {
                                            changeQty(item.id, i, e.target.value)
                                        }} >
                                            <option defaultValue selected value={1}>1</option>
                                            <option value={2}>2</option>
                                            <option value={3}>3</option>
                                            <option value={4}>4</option>
                                            <option value={5}>5</option>
                                            <option value={6}>6</option>
                                            <option value={7}>7</option>
                                            <option value={8}>8</option>
                                            <option value={9}>9</option>
                                            <option value={10}>10</option>
                                        </QtySelector>
                                    </div>
                                    <DisplayedItemPrice>£{  (item.quantity * item.price).toFixed(2)  }</DisplayedItemPrice>
                                    <DisplayedItemPrice onClick={() => deleteItem(item.id, i)} style={{borderRadius: '50%', cursor: 'pointer'}}><FontAwesomeIcon icon='times' /></DisplayedItemPrice>
                                </ItemGrid>
                            )
                        })
                    }
                    {
                        bagItems.length >= 1 &&
                        <ItemGrid>
                            <p></p>
                            <p style={{fontSize: '19px'}}>Total</p>
                            <strong><p style={{fontSize: '19px'}}>£{itemsTotal.toFixed(2)}</p></strong>
                        </ItemGrid>
                    }
                </div>
            </div>

            <CheckoutWrapper>
                <CheckoutRow>
                    <p style={{
                        fontSize: '25px',
                        fontWeight: '800'
                    }}>TOTAL</p>
                    <p style={{
                        fontSize: '25px',
                        // fontWeight: '800'
                    }}>{total}</p>
                </CheckoutRow>
                <CheckoutRow>
                    <p>Sub-total</p>
                    <p>{itemsTotal}</p>
                </CheckoutRow>
                <CheckoutRow>
                    <p>Delivery</p>
                    <select></select>
                </CheckoutRow>
            </CheckoutWrapper>

        </div>
    )
}

export default ShoppingBag
