import React, {useEffect, useState} from 'react';
import Nav from '../Components/Nav/Nav';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
// import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

const Container = styled.div`
    display: flex;
    justify-content: center;

    @media (max-width: 960px) {
        align-items: center;
        flex-direction: column;
    }
`;

const ItemDetails = styled.div`
    display: flex;
    justify-content: flex-start;
    flex-direction: column;
    width: calc(100% - 40px);
    margin: 40px;

    @media (max-width: 960px) {
        width: calc(100% - 15vw);
        margin: 15px 15vw ;
    }
`;

const ItemImage = styled.img`
    height: 70vh;
    width: 60vw;
    object-fit: cover;
    transition: all .4s ease;

    @media (max-width: 960px) {
        height: 60vh;
        width: 85vw;
    }
`;

const SizesContainer = styled.div`
    display: flex;
    flex-wrap: wrap;
    justify-content: center;
    align-items: center;
    margin: 20px;
    max-width: 500px
`;

const SizeOption = styled.p`
    width: 150px;
    height: 50px;
    border: 2px solid black;
    border-radius: 7px;

    @media (max-width: 960px) {
        width: 25vw;
        height: 70px;
    }
`;

const sizeOptions = [
    3, 4, 5, 6, 6.5, 7, 7.5, 8, 8.5, 9, 10, 11, 12, 13
];


function ItemPage(props) {
    const {id, name, img, price} = props.location.state.item;

    const [minimized, setMinimize] = useState(window.innerWidth <= 960);

    const handleResize = () => {
		if (window.innerWidth > 960) {
			setMinimize(false);
		}
		else {
			setMinimize(true);
		}
	}

    useEffect(() => {
		window.addEventListener("resize", handleResize, false);
	}, []);

    return (
        <>
            <Nav />

            <Container>
                {
                    minimized &&
                <ItemDetails>
                    <strong><p style={{fontSize: '28px'}}>{name}</p></strong>
                    <p style={{fontSize: '21px'}}>£{price}</p>
                </ItemDetails>
                }
                <ItemImage src={img} />
                <div>
                    { !minimized &&
                    <ItemDetails>
                        <strong><p style={{fontSize: '28px'}}>{name}</p></strong>
                        <p style={{fontSize: '21px'}}>£{price}</p>
                    </ItemDetails>

                    }
                    <SizesContainer>
                        {
                            sizeOptions.map(size => {
                                return(
                                    <SizeOption>{size}</SizeOption>
                                );
                            })
                        }
                    </SizesContainer>

                </div>
            </Container>


        </>
    )
}

export default ItemPage
