import React, {useEffect, useState} from 'react';
import Nav from '../Components/Nav/Nav';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

const Container = styled.div`
    display: flex;
    justify-content: center;
    margin-top: 10vh;

    @media (max-width: 960px) {
        align-items: center;
        flex-direction: column;
        margin-top: 0;
    }
`;

const ItemDetails = styled.div`
    display: flex;
    justify-content: flex-start;
    flex-direction: column;
    width: calc(100% - 40px);
    margin: 0 0 15px 0;

    @media (max-width: 960px) {
        width: calc(100% - 15vw);
        margin: 15px 15vw ;
    }
`;

const ImagesWrappr = styled.div`
    height: 50vh;
    width: 40vw;

    @media (max-width: 960px) {
        height: 60vh;
        width: 85vw;
    }
`;

const ItemImage = styled.img`
    height: 50vh;
    width: 40vw;
    object-fit: cover;
    transition: all .4s ease;

    @media (max-width: 960px) {
        height: 60vh;
        width: 85vw;
    }
`;

const SizesContainer = styled.div`
    display: flex;
    flex-wrap: wrap;
    justify-content: center;
    align-items: center;
    width: 100%
`;

const SizeOptionWrapper = styled.p`
    height: 48px;
    width: 120px;
    border: 1px solid black;
    border-radius: 7px;
    margin: 3px;

    display: flex;
    justify-content: center;
    align-items: center;
    transition: all .2s ease;
    cursor: pointer;

    @media (max-width: 960px) {
        width: 30%;
        height: 52px;
    }

    &:hover {
        background-color: black;
    }

    &:hover .size-option-text {
        color: white;
    }
`;

const SizeSection = styled.div`
    margin-left: 70px;
    width: 380px;

    @media (max-width: 960px) {
        margin-left: 0;
        margin-top: 65px;
        width: 90vw;
        display: flex;
        flex-direction: column;
        justify-content: center;
        align-items: center;
    }
`;

const AddToBag = styled.button`
    width: 100%;
    height: 50px;
    background-color: black;
    color: white;
    border : 0;
    border-radius: 30px;
    margin: 25px 0 0 0;
    transition: all .3s ease;

    &: hover {
        opacity: .8;
    }

    @media (max-width: 960px) {
        width: 70%;
    }
`;

const LikeItem = styled.button`
    width: 100%;
    height: 50px;
    background-color: white;
    border-style:none;
    border : 1px grey solid;
    border-radius: 30px;
    margin: 12px 0;
    transition: all .3s ease;

    &: hover {
        opacity: .8;
    }

    @media (max-width: 960px) {
        width: 70%;
    }
`;

export const sizeOptions = [
    3, 4, 5, 6, 6.5, 7, 7.5, 8, 8.5, 9, 10, 11, 12, 13
];


function ItemPage(props) {
    const {id, name, img, price} = props.location.state.item;

    const [selectedSize, selectSize] = useState(null);
    const [sizeNotSelected, displayError] = useState(true);
    const [ATBPressed, setATBPressed] = useState(false);
    const [minimized, setMinimize] = useState(window.innerWidth <= 960);

    const handleResize = () => {
		if (window.innerWidth > 960) {
			setMinimize(false);
		}
		else {
			setMinimize(true);
		}
	}

    useEffect(() => {
		window.addEventListener("resize", handleResize, false);
	}, []);

    return (
        <>
            <Nav />

            <Container>
                {
                    minimized &&
                <ItemDetails>
                    <strong><p style={{fontSize: '28px'}}>{name}</p></strong>
                    <p style={{fontSize: '21px'}}>£{price}</p>
                </ItemDetails>
                }
                <div>
                    <ItemImage src={img[0]} />
                    <ItemImage src={img[1]} />
                </div>

                <SizeSection>
                    { !minimized &&
                    <ItemDetails>
                        <strong><p style={{fontSize: '28px'}}>{name}</p></strong>
                        <p style={{fontSize: '21px'}}>£{price}</p>
                    </ItemDetails>

                    }
                    <strong><p className={sizeNotSelected && ATBPressed && 'select-size-error'} style={{margin: '5px', fontSize: '17px'}}>{ sizeNotSelected && ATBPressed ? 'Please select a size' : 'Sizes'}</p></strong>
                    <SizesContainer className={sizeNotSelected && ATBPressed && 'select-size-error-border'}>
                        {
                            sizeOptions.map(size => {
                                return(
                                    <SizeOptionWrapper onClick={() => {selectSize(size); displayError(false)}} className={ selectedSize === size && 'selected-size-wrapper'}><p className={selectedSize === size ? 'size-option-text selected-size' : 'size-option-text'} style={{marginBottom: '0px', transition: 'all .2s ease'}}>{size}</p></SizeOptionWrapper>
                                );
                            })
                        }
                    </SizesContainer>
                    <AddToBag onClick={() => setATBPressed(true)}>Add To Bag<FontAwesomeIcon icon='shopping-bag' style={{width: '20px', marginLeft: '6px'}}/></AddToBag>
                    <LikeItem>Like</LikeItem>

                </SizeSection>
            </Container>


        </>
    )
}

export default ItemPage
