import React from 'react';
import { useState, useEffect } from 'react';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import styled from 'styled-components';



const FilterButton = styled.p`
    color: ${props => props.color};
    background-color: ${props => props.bg};
    font-size: 14px;
    padding: 5px 23px;
    border: 2px solid black;
    border-radius: 15px;
    font-weight: 550;
    width: fit-content;
    cursor: pointer;
    transition: all 0.3s ease;
    &:hover {
        color: white;
        background-color: black;
    }
`;

const HR  = styled.p`
    margin-top: 15px;
    border-top: solid black 2px;
    width: 300px;
`;

const CheckboxOption = styled.input`
    height: 20px;
    width: 20px;
    color: black;
`;

const filtersData = [
    {
        filter: 'Gender',
        options: [
            'Male', 'Female'
        ]
    },
    {
        filter: 'Kids',
        options: [
            'Boys', 'Girls'
        ]
    },
    {
        filter: 'Shop By Price',
        options: [
            'Under £50', '£50 - £100', '£100 - £150', 'Over £150'
        ]
    },
    {
        filter: 'Sports',
        options: [
            'Lifestyle', 'Runnning', 'Football', 'Training & Gym', 'Basketball'
        ]
    }
]

const FiltersColumn = styled.div`
    width: 260px;
    display: flex;
    flex-direction: column;

`;

function Filters() {
    const [clicked, handleClick] = useState(false);
    const [genderClicked, handleGenderClick] = useState(false);
    const [priceClicked, handlePriceClick] = useState(false);
    // const [clicked, handleClick] = useState(false);

    return (
        <div style={{display: 'row', flexDirection: 'column', width: '100%'}}>
            {
                clicked && 
                <>


            <HR></HR>
            </>
            }

            <FiltersColumn>
                {
                    filtersData.map(filter => {
                        return (
                            <div style={{margin: '20px'}}>
                                <label style={{marginBottom: '10px'}}>{filter.filter}</label>
                                {
                                    filter.options.map(option => {
                                        return (
                                            <div style={{display: 'flex', alignItems: 'center', marginBottom: '7px'}}>
                                                <CheckboxOption type='checkbox'></CheckboxOption>
                                                <label style={{marginLeft: '7px'}}>{option}</label>
                                            </div>
                                        )
                                    })
                                }
                            </div>
                        )
                    })
                }
            </FiltersColumn>


            {/* <FilterButton color={clicked ? 'white' : 'black'} bg={clicked ? 'black' : 'white'} onClick={() => handleClick((!clicked))}>Filters   <FontAwesomeIcon icon={clicked ? 'caret-up' : 'caret-down'} /></FilterButton> */}
        </div>
    )
}

export default Filters
