import React from 'react';
import { useState } from 'react';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import styled from 'styled-components';



const FilterButton = styled.p`
    color: ${props => props.color};
    background-color: ${props => props.bg};
    font-size: 14px;
    padding: 5px 23px;
    border: 2px solid black;
    border-radius: 15px;
    font-weight: 550;
    width: fit-content;
    cursor: pointer;
    transition: all 0.3s ease;
    &:hover {
        color: white;
        background-color: black;
    }
`;

// const HR  = styled.p`
//     margin-top: 15px;
//     border-top: solid black 2px;
//     width: 300px;
// `;

const CheckboxOption = styled.input`
    height: 20px;
    width: 20px;
    // color: black;
    // filter: sepia(0) brightness(50%) invert(0.7) hue-rotate(100deg);
`;

const filtersData = [

    [
        'Male', 'Female'
    ],
    [
        'Boys', 'Girls'
    ],
    [
        'Under £50', '£50 - £100', '£100 - £150', 'Over £150'
    ],
    [
        'Lifestyle', 'Runnning', 'Football', 'Training & Gym', 'Basketball'
    ],
    [
        'Jordan Air', 'Air Max', 'Air Force'
    ]
]

const FiltersColumn = styled.div`
    width: 260px;
    display: flex;
    flex-direction: column;

`;

const HR = styled.hr`
    border: .5px solid black;
    width: 150px;
    margin-left: 20px;
`;

function Filters() {
    const [genderClicked, handleGenderClick] = useState(true);
    const [priceClicked, handlePriceClick] = useState(true);
    const [kidsClicked, handleKidsClick] = useState(true);
    const [sportsClicked, handleSportsClick] = useState(true);
    const [collectionsClicked, handleCollectionsClick] = useState(true);
    const [sizeClicked, handleSizeClick] = useState(true);

    return (
        <div style={{display: 'row', flexDirection: 'column', width: '100%'}}>

            <FiltersColumn>
                <div style={{margin: '20px'}}>
                    <label style={{marginBottom: '10px', cursor: 'pointer'}} onClick={() => handleGenderClick(!genderClicked)} >Gender <FontAwesomeIcon icon={genderClicked ? 'caret-up' : 'caret-down'} /></label>
                    {   genderClicked &&
                        filtersData[0].map(option => {
                            return (
                                <div style={{display: 'flex', alignItems: 'center', marginBottom: '7px'}}>
                                    {/* <CheckboxOption type='checkbox' className='checkbox'></CheckboxOption>
                                    <label style={{marginLeft: '7px'}}>{option}</label> */}

                                    <div class="form-group">
                                        <input type="checkbox" id="html"/>
                                        <label for="html">{option}</label>
                                    </div>
                                </div>
                            )
                        })
                    }
                </div>

                {/* <HR></HR> */}

                <div style={{margin: '20px'}}>
                <label style={{marginBottom: '10px', cursor: 'pointer'}} onClick={() => handleKidsClick(!kidsClicked)} >Kids <FontAwesomeIcon icon={kidsClicked ? 'caret-up' : 'caret-down'} /></label>
                    {   kidsClicked &&
                        filtersData[1].map(option => {
                            return (
                                <div style={{display: 'flex', alignItems: 'center', marginBottom: '7px'}}>
                                    <CheckboxOption type='checkbox'></CheckboxOption>
                                    <label style={{marginLeft: '7px'}}>{option}</label>
                                </div>
                            )
                        })
                    }
                </div>
                <div style={{margin: '20px'}}>
                <label style={{marginBottom: '10px', cursor: 'pointer'}} onClick={() => handlePriceClick(!priceClicked)} >Shop By Price <FontAwesomeIcon icon={priceClicked ? 'caret-up' : 'caret-down'} /></label>
                    {   priceClicked &&
                        filtersData[2].map(option => {
                            return (
                                <div style={{display: 'flex', alignItems: 'center', marginBottom: '7px'}}>
                                    <CheckboxOption type='checkbox'></CheckboxOption>
                                    <label style={{marginLeft: '7px'}}>{option}</label>
                                </div>
                            )
                        })
                    }
                </div>
                <div style={{margin: '20px'}}>
                <label style={{marginBottom: '10px', cursor: 'pointer'}} onClick={() => handleSportsClick(!sportsClicked)} >Sport <FontAwesomeIcon icon={sportsClicked ? 'caret-up' : 'caret-down'} /></label>
                    {   sportsClicked &&
                        filtersData[3].map(option => {
                            return (
                                <div style={{display: 'flex', alignItems: 'center', marginBottom: '7px'}}>
                                    <CheckboxOption type='checkbox'></CheckboxOption>
                                    <label style={{marginLeft: '7px'}}>{option}</label>
                                </div>
                            )
                        })
                    }
                </div>
                <div style={{margin: '20px'}}>
                <label style={{marginBottom: '10px', cursor: 'pointer'}} onClick={() => handleCollectionsClick(!collectionsClicked)} >Collections <FontAwesomeIcon icon={collectionsClicked ? 'caret-up' : 'caret-down'} /></label>
                    {   collectionsClicked &&
                        filtersData[4].map(option => {
                            return (
                                <div style={{display: 'flex', alignItems: 'center', marginBottom: '7px'}}>
                                    <CheckboxOption type='checkbox'></CheckboxOption>
                                    <label style={{marginLeft: '7px'}}>{option}</label>
                                </div>
                            )
                        })
                    }
                </div>
            </FiltersColumn>
        </div>
    )
}

export default Filters
