import React from 'react';
import  { Swiper , SwiperSlide}  from 'swiper/react';
import SwiperCore, { Navigation } from 'swiper';
import 'swiper/swiper-bundle.css';
import styled from 'styled-components';

const Category = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    text-align: center;
    margin: 10px;
    cursor: pointer;
    transform: all 0.7s ease;
    &:hover .category-img {
        transform: scale(1.05);
    }
`;

const CategoryImage = styled.img`
    height: 166px;
    width: 166px;
    object-fit: cover;
    border-radius: 50%;
    margin: 15px;
    transform: all 0.7s ease;
`;

const categories = [
    {
        category: 'Men',
        img: 'https://i8.amplience.net/i/jpl/jd_368699_a?qlt=92&w=950&h=1212&v=1&fmt=webp'
    },
    {
        category: 'Women',
        img: 'https://static.nike.com/a/images/c_limit,w_592,f_auto/t_product_v1/241c485a-27a6-46cb-b951-2acc64563205/sportswear-tech-fleece-windrunner-hoodie-3jcHXn.png'
    },
    {
        category: 'Men',
        img: 'https://i8.amplience.net/i/jpl/jd_368699_a?qlt=92&w=950&h=1212&v=1&fmt=webp'
    },
    {
        category: 'Women',
        img: 'https://static.nike.com/a/images/c_limit,w_592,f_auto/t_product_v1/241c485a-27a6-46cb-b951-2acc64563205/sportswear-tech-fleece-windrunner-hoodie-3jcHXn.png'
    },
    {
        category: 'Men',
        img: 'https://i8.amplience.net/i/jpl/jd_368699_a?qlt=92&w=950&h=1212&v=1&fmt=webp'
    },
    {
        category: 'Women',
        img: 'https://static.nike.com/a/images/c_limit,w_592,f_auto/t_product_v1/241c485a-27a6-46cb-b951-2acc64563205/sportswear-tech-fleece-windrunner-hoodie-3jcHXn.png'
    },
    {
        category: 'Men',
        img: 'https://i8.amplience.net/i/jpl/jd_368699_a?qlt=92&w=950&h=1212&v=1&fmt=webp'
    },
    {
        category: 'Women',
        img: 'https://static.nike.com/a/images/c_limit,w_592,f_auto/t_product_v1/241c485a-27a6-46cb-b951-2acc64563205/sportswear-tech-fleece-windrunner-hoodie-3jcHXn.png'
    }
];

SwiperCore.use([Navigation]);

function SwiperCategories() {
    return (
        <Swiper
         speed={500}
         slidesPerView={3}
         spaceBetween={20}
        //  navigation={{
        //     nextEl: '.swiper-button-next'
        //     // prevEl: '.swiper-button-prev'
        //  }}
         navigation
        >
            {
                categories.map((category, i) => {
                    return (
                        <SwiperSlide>
                            <Category>
                                <CategoryImage className='category-img' src={category.img} />
                                <h5 style={{color: 'white', margin: '10px'}}>{category.category}</h5>
                            </Category>
                        </SwiperSlide>
                    );
                })
            }
        </Swiper>
    )
}

export default SwiperCategories
