import React, {useEffect, useState} from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

const Navbar = styled.div`
	min-height: 55px;
	backround-color: white;
	align-items: center;
`;

const NavSection = styled.ul`
	list-style-type: none;
`;

const li = styled.li`
	color: black;
	width: 10%;
	height: 40px;
	text-decoration: none;
	padding-top: 7px;
	margin: 7px;
	font-size: 18px;
	&:hover {
		color: #575757;
	}
`;


const AccountNav = styled.div`
	min-height: 35px;
	background-color: #dedede;
	display: flex;
	justify-content: flex-end;
	align-items: center;
`;

const JoinUs = styled.p`
	margin: 0;
	margin-right: 10px;
	font-size: 0.8rem;
	height: 100%;
	&:hover {
		color: #595959;
	}
`;

const SignIn = styled.p`
	margin: 0px 20px 0px 10px;
	font-size: 0.8rem;
	height: 100%;
	&:hover {
		color: #595959;
	}
`;


function Nav() {

	const [minimized, setMinimize] = useState(window.innerWidth <= 960);
	const [clicked, setClick] = useState(false);

	const handleResize = () => {setMinimize(window.innerWidth <= 960);}

	useEffect(() => {
		window.addEventListener("resize", handleResize(), false);
	}, []);

    return (
		<>	
			<div className={minimized ? 'active' : 'main'}>
				<FontAwesomeIcon icon='bars' onClick={setClick(!clicked)}/>

				<Link style={{marginTop: '10px', textDecoration: 'none', fontSize: '22px', color: 'black', cursor: 'pointer', marginLeft: '20px', fontWeight: '500', display: 'inline-block'}}>Sneaker Galore</Link>

				<ul className={clicked ? 'active-nav' : 'main-nav'}>
					<li className={clicked ? 'active-link' : 'main-link'}>Home</li>
					<li className={clicked ? 'active-link' : 'main-link'}>Featured Collections</li>
					<li className={clicked ? 'active-link' : 'main-link'}>Shop</li>
				</ul>

				<ul className={clicked ? 'active-nav' : 'main-nav'}>
					<li style={{cursor: 'pointer'}}>Home</li>
					<li style={{cursor: 'pointer'}}>Featured Collections</li>
				</ul>


			</div>

			<AccountNav>
				<Link
					style={{textDecoration: 'none', color: 'black', height: '100%'}}
				><JoinUs>Join Us</JoinUs></Link>
				|
				<Link
					style={{textDecoration: 'none', color: 'black'}}
				><SignIn>Sign In</SignIn></Link>
			</AccountNav>
		</>
    );
}

export default Nav;
