import React, {useEffect, useState} from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import './Nav.css';

const AccountNav = styled.div`
	min-height: 35px;
	background-color: #dedede;
	display: flex;
	justify-content: flex-end;
	align-items: center;
`;

const JoinUs = styled.p`
	margin: 0;
	margin-right: 10px;
	font-size: 0.8rem;
	height: 100%;
	&:hover {
		color: #595959;
	}
`;

const SignIn = styled.p`
	margin: 0px 20px 0px 10px;
	font-size: 0.8rem;
	height: 100%;
	&:hover {
		color: #595959;
	}
`;


function Nav() {

	const [minimized, setMinimize] = useState(window.innerWidth <= 960);
	const [clicked, setClick] = useState(false);
	const [dropdownHover, onDropdownToggle] = useState(true);

	const handleResize = () => {
		if (window.innerWidth > 960) {
			setMinimize(false);
			setClick(false);
		}
		else {
			setMinimize(true);
		}
	}

	useEffect(() => {
		window.addEventListener("resize", handleResize, false);
	}, []);

    return (
		<>	
			<div className='nav-wrapper'>
				<div className={clicked ? 'active' : !clicked && minimized ? 'idle' : 'main'}>
					<Link style={{marginTop: '17px', textDecoration: 'none', fontSize: '22px', color: 'black', cursor: 'pointer', marginLeft: '20px', fontWeight: '500', display: 'inline-block'}}>Sneaker Galore</Link>
					<FontAwesomeIcon icon={clicked ? 'times' : 'bars'} onClick={() => setClick(!clicked)} className={minimized ? 'bars' : 'bars-main'}/>


					<ul className={clicked ? 'active-nav' : !clicked && minimized ? 'idle-nav' : 'main-nav'}>
						<li className={clicked ? 'active-link' : 'main-link'}><p style={{marginBottom:'0', margin: '10px'}}>Home</p></li>
						<li className={clicked ? 'active-link' : 'main-link'} onMouseEnter={() => onDropdownToggle(true)} onMouseLeave={() => onDropdownToggle(false)}><p style={{marginBottom:'0', margin: '10px'}}>Shop Now  <FontAwesomeIcon icon={dropdownHover ? 'caret-up' : 'caret-down'} /></p> 
							<ul
							className={
								dropdownHover && minimized && clicked ? 'active-dropdown-nav' //minimized and clicked
								: dropdownHover && !minimized ? 'main-dropdown-nav' //clicked but not minimized
								: 'idle-dropdown-nav' //not clicked at all (doesn't matter minimized or not)
								}>
								<li className='main-dropdown-link'>Men's</li>
								<li className='main-dropdown-link'>Women's</li>
								<li className='main-dropdown-link'>Children's</li>
								<li className='main-dropdown-link'>Lifestyle</li>
								<li className='main-dropdown-link'>Running</li>
								<li className='main-dropdown-link'>Basketball</li>
								<li className='main-dropdown-link'>Football</li>
								<li className='main-dropdown-link'>Jordan</li>
								<li className='main-dropdown-link'>Accessories</li>
							</ul>
						</li>
						{/* <li className={clicked ? 'active-link' : 'main-link'}><p style={{marginBottom:'0', margin: '10px'}}>Shop</p></li> */}
					</ul>

					<ul className={clicked ? 'active-nav' : !clicked && minimized ? 'idle-nav' : 'main-nav'}>
						<li className={clicked ? 'active-link' : 'main-link'}>
							<div id='search-bar-wrapper'>
								<FontAwesomeIcon icon='search' id='search-bar-icon'/>				
								<input type='text' id='nav-search' placeholder='Search'/>
							</div>
						</li>
						<li >
							<FontAwesomeIcon icon='shopping-bag' className={clicked ? 'active-icon' : 'nav-icon'}/>
							<FontAwesomeIcon icon='heart' className={clicked ? 'active-icon' : 'nav-icon'}/>
						</li>
					</ul>


				</div>

				<AccountNav>
					<Link
						style={{textDecoration: 'none', color: 'black', height: '100%'}}
					><JoinUs>Join Us</JoinUs></Link>
					|
					<Link
						style={{textDecoration: 'none', color: 'black'}}
					><SignIn>Sign In</SignIn></Link>
				</AccountNav>
			</div>
		</>
    );
}

export default Nav;
