import React, { useState, useEffect } from 'react'
import styled from 'styled-components';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import  { Swiper , SwiperSlide}  from 'swiper/react';
import SwiperCore, { Scrollbar, Mousewheel, Pagination } from 'swiper';
import 'swiper/swiper-bundle.css';

import airJordan from '../../Resources/air-jordan-hover.png';
import airMax from '../../Resources/air-max-hover.png';
import airForce from '../../Resources/air-force-hover.jpg';


const BestSellerTitle = styled.p`
    font-size: 1.4rem;
    color: white;
    margin-bottom: 0;
    background-color: black;
    text-align: center;
    padding: 25px;
    padding-top: 50px;
`;

const Card = styled.div`
    height: 400px;
    width: 550px;
    display: grid;
    grid-template-columns: 1.15fr 1fr;
    margin: 20px;
    background-image: url(${props => props.bg});
    background-position: center;
    background-size: 550px 400px;
    object-fit: cont;
    border-radius: 3px;
    cursor: pointer;
    transition: all 0.3s ;
    &:hover {
        background-position: 50% calc(50% - 23px);
    };
    &:hover .see-collection {
        color: #7f1e1d;
        transform: translate(10px);
    };
`;

const CardTextContainer = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    text-align: center;
`;

const SeeCollection = styled.p`

    margin: 20px;
    cursor: pointer;
    transition: all 0.3s ease-in-out;
`;

SwiperCore.use([Scrollbar, Mousewheel]);

function FeaturedCollections() {

    const [minimized, setMinimize] = useState(window.innerWidth <= 1232);

    const handleResize = () => {
        setMinimize(window.innerWidth <= 1232)
    }

    useEffect(() => {
        window.addEventListener("resize", handleResize, false);
    }, []);

    return (
        <>
            <BestSellerTitle>Our Best Selling Collections</BestSellerTitle>
            <Swiper
                scrollbar={{ draggable: true }}
                mousewheel={{
                    draggable: minimized ? true : false
                }}
                speed={500}
                slidesPerView={minimized ? 1 : 3}
                style={{backgroundColor: 'black'}}
            >
                <SwiperSlide scroll>
                    <Card bg={airJordan}>
                        <CardTextContainer>
                            <h3 className='featured-collection-title'>Air Jordan</h3>
                            <SeeCollection className='see-collection'>See Collection<FontAwesomeIcon icon='arrow-right' style={{marginLeft: '10px'}}/></SeeCollection>
                        </CardTextContainer>
                    </Card>
                </SwiperSlide>
                <SwiperSlide>
                    <Card bg={airMax}>
                        <CardTextContainer>
                            <h3 className='featured-collection-title'>Air Max</h3>
                            <SeeCollection className='see-collection'>See Collection<FontAwesomeIcon icon='arrow-right' style={{marginLeft: '10px'}}/></SeeCollection>
                        </CardTextContainer>
                    </Card>
                </SwiperSlide>
                <SwiperSlide>
                    <Card bg={airForce}>
                        <CardTextContainer>
                            <h3 className='featured-collection-title'>Air Force</h3>
                            <SeeCollection className='see-collection'>See Collection<FontAwesomeIcon icon='arrow-right' style={{marginLeft: '10px'}}/></SeeCollection>
                        </CardTextContainer>
                    </Card>
                </SwiperSlide>
            </Swiper>

        </>
    )
}

export default FeaturedCollections
