// Libraries
import React from 'react';
import styled from 'styled-components';

// Components
import HeroSlider, {
  Slide,
  Nav,
  OverlayContainer,
} from 'hero-slider';
// import { H2 } from 'components/UI/Text';

// Assets
import heroBg1 from '../Resources/hero-bg.jpg';
import heroBg2 from '../Resources/hero-bg-2.png';

const StyledOverlayContainer = styled(OverlayContainer)`
  &&& {
    display: flex;
    flex-flow: column;
    align-items: center;
    justify-content: center;
    width: 100%;
    height: 100%;
    margin: 0 auto;
    background-color: rgba(0, 0, 0, 0.33);
    text-align: center;
    H2, H3 {
      margin: 0 36px;
    }
  }
`;

const Button = styled.button`
//   /* Adapt the colors based on primary prop */
//   background: ${props => props.primary ? "white" : "transparent"};
//   color: ${props => props.primary ? "white" : "white"};

//   cursor: pointer;
//   font-size: 1.5em;
//   margin: 1em;
//   padding: 0.5em 1.25em;
//   border: 4px solid white;
//   border-radius: 3px;
//   &:hover {

//   }
    background: transparent;
    color: #FFF;
    border: 4px solid white;
    border-radius: 0px;
    padding: 18px 36px;
    display: inline-block;
    font-family: "Lucida Console", Monaco, monospace;
    font-weight: 2rem;
    font-size: 14px;
    letter-spacing: 1px;
    cursor: pointer;
    box-shadow: inset 0 0 0 0 white;
    -webkit-transition: ease-out 0.4s;
    -moz-transition: ease-out 0.4s;
    transition: ease-out 0.4s;
    &:hover {
        box-shadow: inset 400px 0 0 0 white;
        color: black;
    }
`;

export default function AutoplayButtonSlider() {
  return (
    <HeroSlider
      slidingAnimation="left_to_right"
      orientation="horizontal"
      set
      initialSlide={1}
      style={{
        color: '#FFF',
      }}
      settings={{
        slidingDuration: 750,
        slidingDelay: 100,
        shouldAutoplay: true,
        shouldDisplayButtons: true,
        autoplayDuration: 2000,
        height: '90vmin',
      }}
    >
      <StyledOverlayContainer>
        <Button>Shop Now</Button>
      </StyledOverlayContainer>

      <Slide
        shouldRenderMask
        background={{
          backgroundColor: '#8A8A8A',
          maskBackgroundBlendMode: 'luminosity',
          backgroundImage: heroBg1,
          backgroundAnimation: 'fade',
        }}
      />

      <Slide
        shouldRenderMask
        background={{
          backgroundColor: '#8A8A8A',
          maskBackgroundBlendMode: 'luminosity',
          backgroundImage: heroBg2,
          backgroundAnimation: 'fade',
        }}
      />

      {/* <Slide
        shouldRenderMask
        background={{
          backgroundColor: '#8A8A8A',
          maskBackgroundBlendMode: 'luminosity',
          backgroundImage: hakone,
          backgroundAnimation: 'fade',
        }}
      />

      <Slide
        shouldRenderMask
        background={{
          backgroundColor: '#8A8A8A',
          maskBackgroundBlendMode: 'luminosity',
          backgroundImage: byodoInTemple,
          backgroundAnimation: 'fade',
        }}
      /> */}

      <Nav />
    </HeroSlider>
  );
}
