import React from 'react'
import  { Swiper , SwiperSlide}  from 'swiper/react';
import SwiperCore, { Navigation, Mousewheel, Autoplay } from 'swiper';
import 'swiper/swiper-bundle.css';
import styled from 'styled-components';

SwiperCore.use([Navigation, Mousewheel, Autoplay]);

const Wrapper = styled.div`
    width: 100%;
    text-align: center;
    background-color: black;
`;

const CategorySectionTitle = styled.p`
    display: inline-block;
    width: 70%;
    font-size: 1.4rem;
    color: white;
    margin-bottom: 0;
    background-color: black;
    padding: 40px;
    margin-top: 50px;
    border-top: 1px solid white;
    font-weight: 600;
`;

const ItemCard = styled.div`
    height: 400px;
    width: 200px;
    background-color: white;
`;

function FeaturedProducts() {





    return (
        <Wrapper>
            <CategorySectionTitle>Gear Up</CategorySectionTitle>
            <Swiper
                scrollbar={{ draggable: true }}
                mousewheel
                speed={500}
                autoplay
                slidesPerView={minimized ? 1 : 3}
                style={{backgroundColor: 'black'}}
            >
                <SwiperSlide scroll>
                    <Card bg={airJordan}>
                        <CardTextContainer>
                            <strong><h3 className='featured-collection-title'>Air Jordan</h3></strong>
                            <SeeCollection className='see-collection'>See Collection<FontAwesomeIcon icon='arrow-right' style={{marginLeft: '10px'}}/></SeeCollection>
                        </CardTextContainer>
                    </Card>
                </SwiperSlide>
                <SwiperSlide>
                    <Card bg={airMax}>
                        <CardTextContainer>
                            <h3 className='featured-collection-title'>Air Max</h3>
                            <SeeCollection className='see-collection'>See Collection<FontAwesomeIcon icon='arrow-right' style={{marginLeft: '10px'}}/></SeeCollection>
                        </CardTextContainer>
                    </Card>
                </SwiperSlide>
                <SwiperSlide>
                    <Card bg={airForce}>
                        <CardTextContainer>
                            <h3 className='featured-collection-title'>Air Force</h3>
                            <SeeCollection className='see-collection'>See Collection<FontAwesomeIcon icon='arrow-right' style={{marginLeft: '10px'}}/></SeeCollection>
                        </CardTextContainer>
                    </Card>
                </SwiperSlide>
            </Swiper>

        </Wrapper>
    )
}

export default FeaturedProducts
