import React, { Component } from 'react';
import Hero from '../../Components/Hero';
import Nav from '../../Components/Nav/Nav';
import styled from 'styled-components';

import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

// import introRight from '../../Resources/home-intro-right.jpg';
import i1 from '../../Resources/hero-bg.png';

import airJordan from '../../Resources/air-jordan.png';
import airMax from '../../Resources/air-max.png';


import airJordanHover from '../../Resources/air-jordan-hover.png';
import airMaxHover from '../../Resources/air-max-hover.png';

const DeckContainer = styled.div`
    height: 55vh;
    display: flex;
    justify-content: center;
    align-items: center;
    flex-wrap: wrap;
    background-color: black;
`;

const CardDeck = styled.div`
    display: flex;
    justify-content: center;
    align-items: center;
    flex-wrap: wrap;
    background-color: rgb(211, 211, 211);
`;

const Card = styled.div`
    height: 300px;
    width: 400px;
    display: grid;
    grid-template-columns: 1fr 1fr;
    margin: 20px;
    background-image: url(${props => props.bg});
    background-repeat: no-repeat;
    background-position: center;
    background-size: 400px 300px;
    object-fit: cover;
    border-radius: 5px;
    cursor: pointer;
    &:hover {
        background-image: url(${props => props.bgHover});
    };
    &:hover .featured-collection-title, :&hover .see-collection {
        color: black;
        transform: translate(10px);
    };
    // &:hover .see-collection {
    //     color: black;
    //     transform: translate(10px);
    // };
`;

const CardTextContainer = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    text-align: center;
`;

const SeeCollection = styled.p`
    color: ${props => props.color || 'rgb(211, 211, 211)'};
    margin: 20px;
    cursor: pointer;
    transition: all 0.3s ease-in-out;
    // &:hover {
    //     transform: translate(10px);
    //     color: black;
    // }
`;

class Home extends Component {
    // componentDidMount = () => {
    // }

    render() {
        return (
            <>
                <Nav />
                <Hero />
                <DeckContainer>
                    <CardDeck>
                        <Card bg={airJordan} bgHover={airJordanHover}>
                            <CardTextContainer>
                                <h3 className='featured-collection-title'>Air Max</h3>
                                <SeeCollection className='see-collection'>See Collection<FontAwesomeIcon icon='arrow-right' style={{marginLeft: '10px'}}/></SeeCollection>
                            </CardTextContainer>
                        </Card>
                        <Card bg={airMax} bgHover={airMaxHover}>
                            <CardTextContainer>
                                <h3 style={{color: 'white', margin: '10px'}}>Air Jordan</h3>
                                <SeeCollection >See Collection<FontAwesomeIcon icon='arrow-right' style={{marginLeft: '10px'}}/></SeeCollection>
                            </CardTextContainer>
                        </Card>
                        <Card bg={i1} >
                            <CardTextContainer>
                                <h3 style={{color: 'white', margin: '10px'}}>Air Force</h3>
                                <SeeCollection>See Collection<FontAwesomeIcon icon='arrow-right' style={{marginLeft: '10px'}}/></SeeCollection>
                            </CardTextContainer>
                        </Card>
                    </CardDeck>
                </DeckContainer>
            </>
        )
    }
}

export default Home;
