import React, { useState, useEffect } from 'react'
import { Link } from 'react-router-dom';
import  { Swiper , SwiperSlide}  from 'swiper/react';
import SwiperCore, { Scrollbar } from 'swiper';
import 'swiper/swiper-bundle.css';
import styled from 'styled-components';

SwiperCore.use([Scrollbar]);

const Wrapper = styled.div`
    width: 100%;
    background-color: white;
    padding-bottom: 70px;
`;

const CategorySectionTitle = styled.p`
    font-size: 1.4rem;
    color: black;
    margin-bottom: 0;
    background-color: white;
    padding: 40px;
    padding-left: 80px;
    margin-top: 50px;
    font-weight: 600;
    @media (max-width: 935px) {
        text-align: center;
        padding-left: 0;
    }
`;

const ItemCard = styled.div`
    height: auto;
    width: 370px;
    cursor: pointer;

    &:hover .featured-item-img {
        transform: scale(1.03);
    }
    &:hover .featured-item-detail {
        color: black;
    }

    @media (max-width: 499px) {
        width: 330px;
    }
`;


const ImageWrapper = styled.div`
    display: inline-block;
    overflow: hidden;  
`;

const ItemImage = styled.img`
    height: 370px;
    width: 370x;
    object-fit: cover;
    transition: all .4s ease;
    @media (max-width: 499px) {
        height: 330px;
        width: 330px;
    }
`;

const ItemDetail = styled.p`
    font-size: 17px;
    margin: 0;
    // color: rgb(75, 74, 74);
    color: black;
    transition: all .3s ease;
    font-weight: 500 !important;
`;

const itemCardsData = [
    {   
        id: 1,
        name: `Air Jordan 1 Mid `,
        img: 'https://static.nike.com/a/images/c_limit,w_592,f_auto/t_product_v1/a7fc738b-d2e8-41df-82c2-a08ffed195c6/air-jordan-1-mid-shoes-KXQ4Xj.png',
        price: 114.95
    },
    {   
        id: 2,
        name: 'Air Max 90 SE',
        img: 'https://static.nike.com/a/images/c_limit,w_592,f_auto/t_product_v1/da79427b-35b4-4cc4-b2e8-2fbd5ef37779/air-max-90-se-shoes-4sdQbc.png',
        price: 134.95
    },
    {   
        id: 3,
        name: 'Cosmic Unity (Team)',
        img: 'https://static.nike.com/a/images/c_limit,w_592,f_auto/t_product_v1/7a99de02-8324-48de-9af8-01abdfa8cc5f/cosmic-unity-basketball-shoe-smsQfP.png',
        price: 134.95
    },
    {   
        id: 4,
        name: 'Giannis Immortality',
        img: 'https://static.nike.com/a/images/c_limit,w_592,f_auto/t_product_v1/cec6ebfb-267d-42ae-b4b8-8862075f91e6/giannis-immortality-basketball-shoes-zGxPT5.png',
        price: 72.95
    },
    {   
        id: 5,
        name: 'Mercurial Superfly 8 Elite AG',
        img: 'https://static.nike.com/a/images/c_limit,w_592,f_auto/t_product_v1/211a6a7c-8fd6-4816-bbad-8420b0b509bd/mercurial-superfly-8-elite-ag-football-boot-M59nT2.png',
        price: 244.95
    },
    {   
        id: 6,
        name: 'Free Run 5.0',
        img: 'https://static.nike.com/a/images/c_limit,w_592,f_auto/t_product_v1/db2d6208-ae4e-4af0-a6b7-a19df7f05912/free-run-5-running-shoe-GlfMlr.png',
        price: 99.95
    }
]; 

function FeaturedProducts() {

    const [widthSize, setWidthSize] = useState(window.innerWidth <= 790 ? 'XS' : window.innerWidth <= 1180 ? 'S' : window.innerWidth <= 1600 ? 'M' : 'L');

    const handleResize = () => {
        if (window.innerWidth <= 790) setWidthSize('XS');
        else if (window.innerWidth <= 1180) setWidthSize('S');
        else if (window.innerWidth <= 1600) setWidthSize('M');
        else setWidthSize('L')
    }

    useEffect(() => {
        window.addEventListener("resize", handleResize, false);
    }, []);

    return (
        <>
        <CategorySectionTitle>Gear Up</CategorySectionTitle>
        <Wrapper>
            <Swiper
                scrollbar={{ draggable: true }}
                speed={500}
                slidesPerView={widthSize === 'L' ? 4 : widthSize === 'M' ? 3 : widthSize === 'S' ? 2 : 1}
                spaceBetween={0}
                style={{backgroundColor: 'white', paddingBottom: '80px'}}
            >
                {
                    itemCardsData.map((item, i) => {
                        return (
                            <SwiperSlide>
                                <Link to={{
                                    pathname: '/shop',
                                    state: {category: item.category}
                                }} className='link'>
                                    <ItemCard>
                                        <ImageWrapper>
                                            <ItemImage src={item.img} className='featured-item-img'/>
                                        </ImageWrapper>
                                        <ItemDetail className='featured-item-detail'>{item.name}</ItemDetail>
                                        <ItemDetail className='featured-item-detail'>£{item.price}</ItemDetail>
                                    </ItemCard>
                                </Link>
                            </SwiperSlide>
                        )
                    })
                }
            </Swiper>

        </Wrapper>
        </>
    )
}

export default FeaturedProducts
