import React from 'react';
import Nav from '../Components/Nav/Nav';
import { Link } from 'react-router-dom';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import Filters from '../Components/Filters';
import { ItemCard, ImageWrapper, ItemImage, ItemDetail } from '../Components/FeaturedProducts';
import styled from 'styled-components';

const ItemsWrapper = styled.div`
    max-width: calc(100vw - 260px);
    display: flex;
    justify-content: center;
    flex-wrap: wrap;
    align-items: center;
`;

function Shop() {
    return (
        <>
          <Nav/> 
          <Filters />
          <ItemsWrapper>
            <Link to={{
                pathname: `/shop/${item.id}`,
                state: {category: item.category}
            }} className='link'>
                <ItemCard>
                    <ImageWrapper>
                        <ItemImage src={item.img} className='featured-item-img'/>
                    </ImageWrapper>
                    <ItemDetail className='featured-item-detail'>{item.name}</ItemDetail>
                    <ItemDetail className='featured-item-detail'>Â£{item.price}</ItemDetail>
                </ItemCard>
            </Link>
          </ItemsWrapper>
        </>
    )
}

export default Shop;
