            <div style={{margin: '5%'}}>
                <h1 style={{marginBottom: '30px'}}>Shopping Bag</h1>
                <div style={{marginTop: '30px', borderTop: 'solid rgb(224, 224, 224) 4px', paddingTop: '20px'}}>
                    <ItemGrid>
                        <strong><p>PRODUCT DETAILS</p></strong>
                        <strong><p>QTY</p></strong>
                        <strong><p>TOTAL</p></strong>
                    </ItemGrid>
                    {
                        bagItems.map((item, i) => {
                            console.log(item)
                            return(
                                <ItemGrid>
                                    <div style={{display: 'flex'}}>
                                        <img src={item.img[0]} style={{height: '200px', width: '160px', objectFit: 'contain', background: '#f6f6f6'}} alt='shoe'/>
                                        <div style={{display: 'flex', flexDirection: 'column', justifyContent: 'space-between', marginLeft: '10px'}}>
                                            <div style={{display: 'flex', flexDirection: 'column', fontSize: '16px'}}>
                                                <strong><p style={{fontSize: '18px'}}>{item.name}</p></strong>
                                                <p style={{marginBottom: '5px'}}>Size: {item.size}</p>
                                                <p style={{marginBottom: '5px'}}>£{item.price}</p>
                                            </div>
                                            <a  onClick={() => deleteItem(item.id, i)} style={{fontSize: '15px', color: 'black', borderBottom: '1px solid black', width: 'fit-content', cursor: 'pointer'}}>Delete</a>
                                        </div>
                                    </div>
                                    {/* INCREASE QUANTITY , AND DELETE METHODS NEED TO BE WRITTEN IN APP */}
                                    <div>
                                        <input type='number' defaultValue={item.quantity} style={{width: '50px'}}/>
                                    </div>
                                    <p>£{item.quantity * item.price}</p>
                                </ItemGrid>
                            )
                        })
                    }
                </div>
            </div>

            <div style={{width: '300px', height: '50vh', border: '1px solid black', margin: '5%'}}>
            </div>

        </div>
    )
}

export default ShoppingBag
