import React from 'react';
import { useState } from 'react';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import styled from 'styled-components';

const CheckboxOption = styled.input`
    height: 20px;
    width: 20px;
`;

const filtersData = [

    [
        'Male', 'Female'
    ],
    [
        'Boys', 'Girls'
    ],
    [
        'Under £50', '£50 - £100', '£100 - £150', 'Over £150'
    ],
    [
        'Lifestyle', 'Runnning', 'Football', 'Training & Gym', 'Basketball'
    ],
    [
        'Jordan Air', 'Air Max', 'Air Force'
    ],
    [
        3, 4, 5, 6, 6.5, 7, 7.5, 8, 8.5, 9, 10, 11, 12, 13
    ]
]

const FiltersColumn = styled.div`
    width: 260px;
    display: flex;
    flex-direction: column;

    @media (max-width: 960px) {
        width: 100%;
        flex-direction: row;
        flex-wrap: wrap;
        justify-content: center;
        // align-items: center;
    }

`;

const SizeOption = styled.p`
    padding: 5px 12px;
    border: 1px solid black;
    border-radius: 9px;
    margin: 5px;
    cursor: pointer;
    transition: all .3s ease;
    &:hover {
        background-color: black;
        color: white;
    }
`;

const Filters = props => {
    const [genderClicked, handleGenderClick] = useState(true);
    const [priceClicked, handlePriceClick] = useState(true);
    const [sportsClicked, handleSportsClick] = useState(true);
    const [collectionsClicked, handleCollectionsClick] = useState(true);
    const [sizeClicked, handleSizeClick] = useState(true);

    const {
        selectMale,
        selectFemale,
        selectUnder50,
        select50To100,
        select100To150,
        selectOver150,
        selectLifestyle,
        selectRunning,
        selectFootball,
        selectTraining,
        selectBasketball,
        selectJordan,
        selectMax,
        selectForce,
        selectSize
    } = props.filterMethods;

    const {
        selectedMale,
        selectedFemale,
        selectedUnder50,
        selected50To100,
        selected100To150,
        selectedOver150,
        selectedLifestyle,
        selectedRunning,
        selectedFootball,
        selectedTraining,
        selectedBasketball,
        selectedJordan,
        selectedMax,
        selectedForce,
        selectedSize
    } = props.filterVariables;

    console.log(selectedSize);

    return (
        <div style={{display: 'row', flexDirection: 'column'}}>

            <FiltersColumn>
                {/* Gender */}
                <div style={{margin: '20px'}}>
                    <label style={{marginBottom: '10px', cursor: 'pointer'}} onClick={() => handleGenderClick(!genderClicked)} >Gender <FontAwesomeIcon icon={genderClicked ? 'caret-up' : 'caret-down'} /></label>
                    {   genderClicked &&
                        <>

                        <div style={{display: 'flex', alignItems: 'center', marginBottom: '7px', width: '100px', cursor: 'pointer'}} onClick={() => selectMale(!selectedMale)}>
                            <CheckboxOption type='checkbox' className='checkbox' checked={selectedMale}/>
                            <label style={{marginLeft: '7px', cursor: 'pointer'}}>Male</label>
                        </div>

                        <div style={{display: 'flex', alignItems: 'center', marginBottom: '7px', width: '100px', cursor: 'pointer'}}>
                            <CheckboxOption type='checkbox' className='checkbox'/>
                            <label style={{marginLeft: '7px', cursor: 'pointer'}}>Female</label>
                        </div>

                        </>
                    }
                </div>

                {/* Price */}
                <div style={{margin: '20px'}}>
                <label style={{marginBottom: '10px', cursor: 'pointer'}} onClick={() => handlePriceClick(!priceClicked)} >Shop By Price <FontAwesomeIcon icon={priceClicked ? 'caret-up' : 'caret-down'} /></label>
                    {   priceClicked &&
                        filtersData[2].map(option => {
                            return (
                                <div style={{display: 'flex', alignItems: 'center', marginBottom: '7px'}}>
                                    <CheckboxOption type='checkbox'></CheckboxOption>
                                    <label style={{marginLeft: '7px'}}>{option}</label>
                                </div>
                            )
                        })
                    }
                </div>

                {/* Sports */}
                <div style={{margin: '20px'}}>
                <label style={{marginBottom: '10px', cursor: 'pointer'}} onClick={() => handleSportsClick(!sportsClicked)} >Sport <FontAwesomeIcon icon={sportsClicked ? 'caret-up' : 'caret-down'} /></label>
                    {   sportsClicked &&
                        filtersData[3].map(option => {
                            return (
                                <div style={{display: 'flex', alignItems: 'center', marginBottom: '7px'}}>
                                    <CheckboxOption type='checkbox'></CheckboxOption>
                                    <label style={{marginLeft: '7px'}}>{option}</label>
                                </div>
                            )
                        })
                    }
                </div>

                {/* Collection */}
                <div style={{margin: '20px'}}>
                <label style={{marginBottom: '10px', cursor: 'pointer'}} onClick={() => handleCollectionsClick(!collectionsClicked)} >Collections <FontAwesomeIcon icon={collectionsClicked ? 'caret-up' : 'caret-down'} /></label>
                    {   collectionsClicked &&
                        filtersData[4].map(option => {
                            return (
                                <div style={{display: 'flex', alignItems: 'center', marginBottom: '7px'}}>
                                    <CheckboxOption type='checkbox'></CheckboxOption>
                                    <label style={{marginLeft: '7px'}}>{option}</label>
                                </div>
                            )
                        })
                    }
                </div>

                {/* Size */}
                <div style={{margin: '20px'}}>
                    <label style={{marginBottom: '10px', cursor: 'pointer'}} onClick={() => handleSizeClick(!sizeClicked)} >Size <FontAwesomeIcon icon={sizeClicked ? 'caret-up' : 'caret-down'} /></label>
                    <div style={{display: 'flex', flexWrap: 'wrap'}}>
                        {   sizeClicked &&
                            filtersData[5].map(option => {
                                return (
                                    <SizeOption onClick={() => selectSize(option) } className={selectedSize === option && 'selected-size-wrapper selected-size'}>{option}</SizeOption>
                                )
                            })
                        }
                    </div>
                </div>
            </FiltersColumn>
        </div>
    )
}

export default Filters
