import React, { Component } from 'react';
import Nav from '../../Components/Nav/Nav';
import Hero from '../../Components/Hero';
import FeaturedCollections from '../../Components/FeaturedCollections/FeaturedCollections';
import styled from 'styled-components';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

const CategoryContainer = styled.div`
    display: flex;
    flex-direction: row;
    justify-content: center;
    flex-wrap: wrap;
    text-align: center;
    margin: 10px;
`;

const Category = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    text-align: center;
    margin: 10px;
`;

const CategoryImage = styled.div`
    height: 166px;
    width: 166px;
    border-radius: 50%;
    background-image: url(${props => props.bg});
`;





class Home extends Component {
    // componentDidMount = () => {
    // }

    constructor () {
        super();
        this.state = {
            categories: [
                {
                    category: 'Men',
                    img: 'https://i8.amplience.net/i/jpl/jd_368699_a?qlt=92&w=950&h=1212&v=1&fmt=webp'
                },
                {
                    category: 'Women',
                    img: 'https://static.nike.com/a/images/c_limit,w_592,f_auto/t_product_v1/241c485a-27a6-46cb-b951-2acc64563205/sportswear-tech-fleece-windrunner-hoodie-3jcHXn.png'
                }
            ]
        }
    }

    render() {
        const { categories } = this.state;

        return (
            <>
                <Nav />
                <Hero />
                <FeaturedCollections />
                <div style={{height: '100vh', backgroundColor: 'black'}}>
                    <CategoryContainer>
                    {
                        categories.map((category, i) => {
                            return (
                                <Category>
                                    <CategoryImage bg={category.img} />
                                    <h4>{category.category}</h4>
                                </Category>
                            );
                        })
                    }
                        {/* 
                            Categories:
                                - Men
                                - Women
                                - Kids
                                - Running
                                - Lifestyle
                                - Football
                        */}
                    </CategoryContainer>
                </div>
            </>
        )
    }
}

export default Home;
