import React, { Component } from 'react';
import Nav from '../../Components/Nav/Nav';
import Hero from '../../Components/Hero';
import FeaturedCollections from '../../Components/FeaturedCollections/FeaturedCollections';
import styled from 'styled-components';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import SwiperCategories from '../../Components/SwiperCategories';

const CategoryContainer = styled.div`
    display: flex;
    flex-direction: row;
    justify-content: center;
    flex-wrap: wrap;
    text-align: center;
    // margin: 10px;
`;

const Category = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    text-align: center;
    margin: 10px;
    cursor: pointer;
    transform: all 0.7s ease;
    &:hover .category-img {
        transform: scale(1.05);
    }
`;

const CategoryImage = styled.img`
    height: 166px;
    width: 166px;
    object-fit: cover;
    border-radius: 50%;
    margin: 15px;
    transform: all 0.7s ease;
`;

const CategorySectionTitle = styled.p`
    font-size: 1.4rem;
    color: white;
`;




class Home extends Component {
    // componentDidMount = () => {
    // }

    // constructor () {
    //     super();
    //     this.state = {

    //     }
    // }

    render() {
        const { categories } = this.state;

        return (
            <>
                <Nav />
                <Hero />
                <FeaturedCollections />
                <div style={{height: '100vh', backgroundColor: 'black', textAlign: 'center', padding: '25px'}}>
                    <CategorySectionTitle>Shop By Category</CategorySectionTitle>
                    <CategoryContainer>
                    {
                        categories.map((category, i) => {
                            return (
                                <Category>
                                    <CategoryImage className='category-img' src={category.img} />
                                    <h5 style={{color: 'white', margin: '10px'}}>{category.category}</h5>
                                </Category>
                            );
                        })
                    }
                        {/* 
                            Categories:
                                - Men
                                - Women
                                - Kids
                                - Running
                                - Lifestyle
                                - Football
                        */}
                    </CategoryContainer>
                </div>
               <SwiperCategories /> 
            </>
        )
    }
}

export default Home;
