import React, {useEffect, useState} from 'react';
import Nav from '../Components/Nav/Nav';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

const Container = styled.div`
    display: flex;
    justify-content: center;
    margin-top: 10vh;

    @media (max-width: 960px) {
        align-items: center;
        flex-direction: column;
        margin-top: 0;
    }
`;

const ItemDetails = styled.div`
    display: flex;
    justify-content: flex-start;
    flex-direction: column;
    width: calc(100% - 40px);
    margin: 40px;

    @media (max-width: 960px) {
        width: calc(100% - 15vw);
        margin: 15px 15vw ;
    }
`;

const ItemImage = styled.img`
    height: 50vh;
    width: 40vw;
    object-fit: cover;
    transition: all .4s ease;

    @media (max-width: 960px) {
        height: 60vh;
        width: 85vw;
    }
`;

const SizesContainer = styled.div`
    display: flex;
    flex-wrap: wrap;
    justify-content: center;
    align-items: center;
    max-width: 378px;

    @media (max-width: 960px) {
        max-width: 100%;
        width: 100%;
    }
`;

const SizeOptionWrapper = styled.p`
    height: 48px;
    width: 120px;
    border: 1px solid black;
    border-radius: 7px;
    margin: 3px;

    display: flex;
    justify-content: center;
    align-items: center;
    transition: all .2s ease;
    cursor: pointer;

    @media (max-width: 960px) {
        width: 30%;
        height: 52px;
    }

    &:hover {
        background-color: black;
    }

    &:hover .size-option-text {
        color: white;
    }
`;

const SizeSection = styled.div`
    margin-left: 70px;
    width: 378px;

    @media (max-width: 960px) {
        margin-left: 0;
        width: 90vw;
        display: flex;
        flex-direction: column;
        justify-content: center;
        align-items: center;
    }
`;

const AddToBag = styled.button`
    width: 100%;
    height: 50px;
    background-color: black;
    color: white;
    border : 0;
    border-radius: 10px;
    margin: 20px 0;
    transition: all .3s ease;

    &: hover {
        opacity: .8;
    }
`;

const sizeOptions = [
    3, 4, 5, 6, 6.5, 7, 7.5, 8, 8.5, 9, 10, 11, 12, 13
];


function ItemPage(props) {
    const {id, name, img, price} = props.location.state.item;

    const [minimized, setMinimize] = useState(window.innerWidth <= 960);

    const handleResize = () => {
		if (window.innerWidth > 960) {
			setMinimize(false);
		}
		else {
			setMinimize(true);
		}
	}

    useEffect(() => {
		window.addEventListener("resize", handleResize, false);
	}, []);

    return (
        <>
            <Nav />

            <Container>
                {
                    minimized &&
                <ItemDetails>
                    <strong><p style={{fontSize: '28px'}}>{name}</p></strong>
                    <p style={{fontSize: '21px'}}>£{price}</p>
                </ItemDetails>
                }
                <ItemImage src={img} />
                <SizeSection>
                    { !minimized &&
                    <ItemDetails>
                        <strong><p style={{fontSize: '28px'}}>{name}</p></strong>
                        <p style={{fontSize: '21px'}}>£{price}</p>
                    </ItemDetails>

                    }
                    <strong><p style={{margin: '5px', fontSize: '17px'}}>Sizes</p></strong>
                    <SizesContainer>
                        {
                            sizeOptions.map(size => {
                                return(
                                    <SizeOptionWrapper><p className='size-option-text' style={{marginBottom: '0px', transition: 'all .2s ease'}}>{size}</p></SizeOptionWrapper>
                                );
                            })
                        }
                    </SizesContainer>

                    <AddToBag>Add To Bag<FontAwesomeIcon icon='shopping-bag' style={{width: '20px', marginLeft: '3px'}}/></AddToBag>
                </SizeSection>
            </Container>


        </>
    )
}

export default ItemPage
