import React, { Component } from 'react';
import Hero from '../../Components/Hero';
import Nav from '../../Components/Nav/Nav';
import styled from 'styled-components';

import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

// import introRight from '../../Resources/home-intro-right.jpg';
import i1 from '../../Resources/hero-bg.png';
import heroBg2 from '../../Resources/hero-bg-2.png';
import heroBg3 from '../../Resources/hero-bg-3.jpg';

const DeckContainer = styled.div`
    height: 55vh;
    display: flex;
    justify-content: center;
    align-items: center;
    flex-wrap: wrap;
    background-color: black;
`;

const CardDeck = styled.div`
    display: flex;
    justify-content: center;
    align-items: center;
    flex-wrap: wrap;
    background-color: rgb(211, 211, 211);
`;

const Card = styled.div`
    height: auto;
    width: 286px;
    margin: 20px;
    display: grid;
    grid-template-rows: 180px 40px 60px;
    grid-row-gap: 10px;
    background-color: white;
    // border: 1px solid white;
    border-radius: 5px;
    text-align: center;
`;

const NewCard = styled.div`
    height: 300px;
    width: 400px;
    display: grid;
    grid-template-columns: 1fr 1fr;
    margin: 20px;
    background-image: url(${props => props.bg});
    background-repeat: no-repeat;
    background-position: center;
    background-size: 400px 300px;
    object-fit: cover;
    border-radius: 5px;
`;

const CardTextContainer = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    text-align: center;
`;

const CardImg = styled.img`
    width: 100%;
    height: 100%;
    object-fit: cover;
    border-top-left-radius: 5px;
    border-top-right-radius: 5px;
`;

const SeeCollection = styled.p`
    color: rgb(211, 211, 211);
    margin: 20px;
    cursor: pointer;
    transition: all 0.4s ease-in-out;
    &:hover {
        transform: translate(10px);
        color: white;
    }
`;

const CardButton = styled.button`
    height: 35px;
    width: 100px;
    font-size: medium;
    background-color: #7f1e1d;
    color: white;
    border-style: none;
    border-radius: 5px;
    transition: all 0.4s ease-in-out;
    &:hover {
        transform: scale(1.05);
    }
    &:active {
        transform: scale(0.9);
    }
`;

class Home extends Component {
    componentDidMount = () => {
        // fetch("https://v1-sneakers.p.rapidapi.com/v1/sneakers?limit=10", {
        //     "method": "GET",
        //     "headers": {
        //         "Content-Type": 'application/json',
        //         "x-rapidapi-key": "835d4662b7msh2f4103ec598a85ep17a643jsnc2db7484cb09",
        //         "x-rapidapi-host": "v1-sneakers.p.rapidapi.com"
        //     }
        // })
        // .then(resp => resp.json())
        // .then(data => {
        //     console.log(data);
        // })
        // .catch(err => {
        //     console.error(err);
        // });    
    }

    render() {
        return (
            <>
                <Nav />
                <Hero />
                <DeckContainer>
                    <CardDeck>
                        <Card>
                            <CardImg src={i1} />
                            <h4>Air Force</h4>
                            <div>
                                <CardButton>View All</CardButton>
                            </div>
                        </Card>
                        <Card>
                            <CardImg src={heroBg2}/>
                            <h4>Air Max</h4>
                            <div>
                                <CardButton>View All</CardButton>
                            </div>
                        </Card>
                        <Card>
                            <CardImg src={heroBg3}/>
                            <h4>Air Force</h4>
                            <div>
                                <CardButton>View All</CardButton>
                            </div>
                        </Card>
                        <NewCard bg={i1} >
                            <CardTextContainer>
                                <h3 style={{color: 'white', margin: '10px'}}>Air Force</h3>
                                <SeeCollection>See Collection<FontAwesomeIcon icon='arrow-right' style={{marginLeft: '10px'}}/></SeeCollection>
                            </CardTextContainer>
                        </NewCard>
                    </CardDeck>

                </DeckContainer>


            </>
        )
    }
}

export default Home;
